% --- Executes on button press in pushbutton1.
function pushbutton1_Callback(hObject, eventdata, handles)
% hObject    handle to pushbutton1 (see GCBO)
% eventdata  reserved - to be defined in a future version of MATLAB
% handles    structure with handles and user data (see GUIDATA)
axes(handles.axes1);
cla;
m = str2num(get(handles.edit1, 'String'));
n = str2num(get(handles.edit2, 'String'));

popup_sel_index = get(handles.popupmenu1, 'Value');
switch popup_sel_index
    case 1
        cla reset;
        Rejilla_SEL(handles,m,n);
        SNumerica(handles,m,n);
        set(handles.text4,"String","Solución Numérica");
        set(handles.uipanel5,"Visible","on");
        set(handles.uipanel2,"Visible","on");
        set(handles.edit3,"Visible","on");
        set(handles.axes2,"Visible","off");
        set(handles.text4,"Visible","on");
        set(handles.uipanel3,"Visible","off");
        set(handles.uipanel4,"Visible","off");
        
    case 2
        cla reset;
        Rejilla_SEL(handles,m,n);
        Potencial3D(handles,m,n);
        set(handles.text4,"String","Gráfica del Potencial");
        set(handles.edit3,"Visible","off");
        set(handles.axes2,"Visible","on");
        set(handles.text4,"Visible","on");
        set(handles.uipanel5,"Visible","on");
        set(handles.uipanel2,"Visible","on");
        set(handles.uipanel3,"Visible","off");
        set(handles.uipanel4,"Visible","off");
        
    case 3
        cla reset;
        Comparacion3D(handles,m,n);
        set(gca, 'XTick', [], 'YTick', []);
        Comparacion2D(handles,m,n);
        set(handles.axes2,"Visible","off");
        set(handles.uipanel3,"Visible","on");
        set(handles.uipanel4,"Visible","on");
        set(handles.uipanel5,"Visible","on");
        set(handles.axes3,"Visible","on");
        set(handles.axes4,"Visible","on");
        set(handles.uipanel2,"Visible","off");
         
    case 4
        cla reset;
        Rejilla_PIterativo(handles,m,n);
        ProcesoIterativo(handles,m+2,n+2);
        set(handles.axes2,"Visible","off");
        set(handles.text4,"String","Solución con el Proceso Iterativo");
        set(handles.axes2,"Visible","off");
        set(handles.uipanel2,"Visible","on");
        set(handles.uipanel5,"Visible","on");
        set(handles.edit3,"Visible","on");
        set(handles.axes2,"Visible","off");
        set(handles.text4,"Visible","on");
        set(handles.uipanel3,"Visible","off");
        set(handles.uipanel4,"Visible","off"); 
end


